INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65599, 'Cimetidine', 'Nilotinib', 'Moderate', 'INTERVAL: Coadministration of H2 antagonists may significantly decrease the oral bioavailability of nilotinib. The mechanism is decreased solubility of nilotinib due to increased gastric pH. Nilotinib''s solubility is pH dependent and it is insoluble at a pH of 4.5 or higher.', 'DDInter', 'During concomitant use, it is recommended to administer the H2 antagonist 10 hours before and 2 hours after the dose of nilotinib.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65686/', '', 'Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erdafitinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65600, 'Cimetidine', 'Nimodipine', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of calcium channel blockers that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by cimetidine.', 'DDInter', 'Close monitoring of clinical response and tolerance is recommended if cimetidine is used in combination with calcium channel blockers that are metabolized by CYP450 3A4 such as diltiazem, felodipine, nicardipine, nifedipine, nimodipine, nisoldipine, and verapamil. Dose reductions of 40% to 50% may be considered, if necessary. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis. Other H2-receptor antagonists may be safer alternatives, since they have not been shown to significantly affect CYP450 3A4 metabolism.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if cimetidine is used in combination with calcium channel blockers that are metabolized by CYP450 3A4 such as diltiazem, felodipine, nicardipine, nifedipine, nimodipine, nisoldipine, and verapamil.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65687/', '', 'Clevidipine, Mibefradil, Levamlodipine', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Nizatidine, More', 1767369485);
