INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65628, 'Cimetidine', 'Physostigmine', 'Moderate', 'Due to opposing effects, agents that possess anticholinergic activity (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; class IA antiarrhythmics especially disopyramide; carbamazepine; cimetidine; ranitidine) may negate the already small pharmacologic benefits of acetylcholinesterase inhibitors in the treatment of dementia. These agents may also adversely affect elderly patients in general. Clinically significant mental status changes associated with anticholinergic agents can range from mild cognitive impairment to delirium, and patients with Alzheimer''s disease and other dementia are especially sensitive.', 'DDInter', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor. For patients requiring treatment to counteract adverse effects of acetylcholinesterase inhibitor therapy (e.g., gastrointestinal intolerance, urinary problems), an agent without anticholinergic properties should be used whenever possible. Otherwise, a dosage reduction, slower titration, or even discontinuation of the acetylcholinesterase inhibitor should be considered. For patients who are already receiving an acetylcholinesterase inhibitor with anticholinergic agents, every attempt should be made to discontinue the latter or substitute them with less anticholinergic alternatives.', 'Antagonism', 'Drugs that possess anticholinergic activity should generally be avoided in patients with Alzheimer''s disease or other cognitive impairment, regardless of whether they are receiving an acetylcholinesterase inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65715/', '', 'Tinidazole, Dexlansoprazole, Rabeprazole, Metronidazole, Vonoprazan, Amoxicillin, Pantoprazole, Misoprostol, Esomeprazole, Levofloxacin, Sucralfate, More', 'Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, Epinephrine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65629, 'Cimetidine', 'Pimavanserin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as peripheral edema and QT prolongation, and the pimavanserin dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65716/', '', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Ranitidine, More', 'Mesoridazine, Methotrimeprazine, Perphenazine, Promazine, Fluphenazine, Haloperidol, Thiothixene, Trifluoperazine, Amisulpride, Triflupromazine, Loxapine, More', 1767369485);
