INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65633, 'Cimetidine', 'Pirfenidone', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 may increase the plasma concentrations of pirfenidone, especially when used in addition to a moderate or potent CYP450 1A2 inhibitor. Pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', 'DDInter', 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.g., certain fluoroquinolones, oral contraceptives, deferasirox, methoxsalen, mexiletine, thiabendazole, ticlopidine, zileuton) should be avoided. No particular precaution is necessary if pirfenidone and CYP450 2C9, 2C19, 2D6, and/or 2E1 inhibitors are coadministered without a moderate or potent CYP450 1A2 inhibitor.', 'Metabolism', 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65720/', '', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Sucralfate, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65634, 'Cimetidine', 'Piroxicam', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65721/', '', 'Tinidazole, Tetracycline, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Esomeprazole, Clarithromycin, Vonoprazan', 'Phenylbutazone, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, Dimethyl sulfoxide, Phenylbutazone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65635, 'Cimetidine', 'Pitolisant', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase plasma concentrations of pitolisant. The proposed mechanism is decreased clearance of pitolisant due to inhibition of its metabolism via this isoenzyme. Clinically, high plasma levels of pitolisant may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution and clinical monitoring are recommended if pitolisant is used in combination with CYP450 2D6 inhibitors. Patients should be monitored for clinical response and increased adverse effects such as headache, insomnia, nausea, anxiety, increased heart rate, QT prolongation, hallucinations, abdominal pain, upper respiratory tract infections and musculoskeletal pain, and the dosage of pitolisant adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution and clinical monitoring are recommended if pitolisant is used in combination with CYP450 2D6 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65722/', '', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Ranitidine, More', 'Valbenazine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65636, 'Cimetidine', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', 'DDInter', 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65723/', '', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Nizatidine, Esomeprazole, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
