INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65696, 'Cimetidine', 'Tazemetostat', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress. Data are not available for other, less potent inhibitors.', 'DDInter', 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65783/', '', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Nizatidine, More', 'Pertuzumab, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Mitotane, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65697, 'Cimetidine', 'Temsirolimus', 'Moderate', 'Coadministration of temsirolimus with inhibitors of CYP450 3A4 may increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to temsirolimus should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the temsirolimus dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65784/', '', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Sucralfate, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, More', 'Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erdafitinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65698, 'Cimetidine', 'Terbinafine', 'Minor', 'Cimetidine reduces the clearance of terbinafine by 33%. The mechanism probably is inhibition of hepatic enzymes by cimetidine. This interaction is unlikely to be of clinical importance; however, the dosage of terbinafine should be adjusted as necessary.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65785/', '', 'Tinidazole, Tetracycline, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Vonoprazan, Levofloxacin', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Miconazole, Nystatin, Ciclopirox, Griseofulvin, Econazole, Flucytosine, Tioconazole, Chlorphenesin, Griseofulvin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65699, 'Cimetidine', 'Terfenadine', 'Major', 'Cimetidine inhibits the CYP450 isoenzymes and may interfere with metabolism of the antihistamines terfenadine and astemizole. The accumulation of either of these drugs may result in a prolongation of the QT interval and could lead to the development of ventricular arrhythmias such as torsades de pointes. One case of drug-induced torsades de pointes has been reported in a patient receiving cimetidine and terfenadine. The patient was also receiving other medications including chlorpheniramine, propoxyphene and acetaminophen.', 'DDInter', 'Until more information is available, it may be best to avoid this combination. Cetirizine, fexofenadine, or loratadine may be safer antihistamine alternatives.', 'Metabolism', 'Until more information is available, it may be best to avoid this combination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65786/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, More', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65700, 'Cimetidine', 'Teriflunomide', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic anion transporter 3 (OAT3). The proposed mechanism, based on in vivo data, is decreased clearance due to teriflunomide-mediated inhibition of OAT3 transporter.', 'DDInter', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OAT3. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever teriflunomide is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OAT3.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65787/', '', 'Pantoprazole, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Rifabutin, Nizatidine, Esomeprazole, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
