INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65713, 'Cimetidine', 'Tolmetin', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65800/', '', 'Phenylbutazone, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, Dimethyl sulfoxide, Phenylbutazone', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Esomeprazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65714, 'Cimetidine', 'Tolterodine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered. Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', 'DDInter', 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65801/', '', 'Tinidazole, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Esomeprazole, Levofloxacin', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, Papaverine, Phenyl salicylate, More', 1767369485);
