INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65850, 'Aminophylline', 'Ciprofloxacin', 'Major', 'Coadministration with ciprofloxacin may significantly increase the serum concentrations of theophylline and the associated risk of toxicity. The mechanism is ciprofloxacin inhibition of theophylline metabolism via CYP450 1A2.', 'DDInter', 'The use of theophylline or its salts in combination with ciprofloxacin should generally be avoided. If coadministration is required, theophylline dosage may need to be reduced. Pharmacologic response and serum levels should be closely monitored following initiation, discontinuation or change of dosage of ciprofloxacin, and the theophylline dosage adjusted accordingly. Patients should be advised to contact their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, anorexia, headache, tremor, irritability, confusion, insomnia, seizure, palpitation, and arrhythmia.', 'Metabolism', 'The use of theophylline or its salts in combination with ciprofloxacin should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65937/', '', 'Tinidazole, Secnidazole, Cefixime, Amikacin, Levofloxacin, Polymyxin B, Chloramphenicol, Gentamicin, Chlorhexidine, Neomycin, Gatifloxacin, More', 'Omalizumab, Tezepelumab, Benralizumab, Reslizumab, Dyphylline, Mepolizumab, Bromotheophylline', 1767369485);
