INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65924, 'Eliglustat', 'Ciprofloxacin', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs). In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor. However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs). Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66011/', '', 'Tinidazole, Tetracycline, Cefepime, Norfloxacin, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, More', 'Lipoic acid, Triheptanoin, Teduglutide, Fosdenopterin, Triethylenetetramine, Miglustat, Sapropterin, Levocarnitine, Laronidase, Imiglucerase, Metreleptin, More', 1767369485);
