INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66075, 'Ciprofloxacin', 'Mephenytoin', 'Moderate', 'Ciprofloxacin may increase or decrease plasma phenytoin concentrations during concomitant therapy, increasing the risk of phenytoin toxicity or seizures. In addition, quinolones may lower the seizure threshold. Although not clearly defined, suggested mechanisms for reduced phenytoin concentrations include ciprofloxacin inhibition of phenytoin renal tubular reabsorption resulting in increased phenytoin elimination or an effect by ciprofloxacin on intestinal flora that alters phenytoin enterohepatic recirculation. Other hydantoins may also be affected.', 'DDInter', 'Clinical monitoring of patient response, including serum hydantoin concentrations, is recommended when initiating or discontinuing ciprofloxacin. Patients should be advised to notify their physician if they experience a loss of seizure control or possible symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. Adjustment of hydantoin dosages may be required.', 'Synergism, Excretion', 'Clinical monitoring of patient response, including serum hydantoin concentrations, is recommended when initiating or discontinuing ciprofloxacin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66162/', '', 'Tiagabine, Brivaracetam, Rufinamide, Methsuximide, Paramethadione, Cenobamate, Perampanel, Fenfluramine, Eslicarbazepine, Phensuximide, Methylphenobarbital, More', 'Tinidazole, Tetracycline, Cefepime, Norfloxacin, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, More', 1767369485);
