INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66077, 'Ciprofloxacin', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66164/', '', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Tetracycline, Polymyxin B, Chloramphenicol, Gentamicin, More', 'Loxapine, Molindone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66078, 'Ciprofloxacin', 'Orciprenaline', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66165/', '', 'Tinidazole, Tetracycline, Cefepime, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Amikacin, Tetracycline, Polymyxin B, Chloramphenicol, More', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate, Ephedrine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66079, 'Ciprofloxacin', 'Methadone', 'Major', 'Coadministration with ciprofloxacin may increase the plasma concentrations and pharmacologic effects of methadone. The proposed mechanism is ciprofloxacin inhibition of methadone metabolism via CYP450 3A4 and, to a lesser extent, CYP450 1A2.', 'DDInter', 'Caution is advised if methadone is prescribed in combination with ciprofloxacin. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of ciprofloxacin in patients who are stabilized on their methadone regimen. It may be advisable to avoid using high dosages of methadone (>100 mg/day) with ciprofloxacin. In general, ECG monitoring should be considered for all methadone-treated patients with risk factors such as heart or liver disease; conduction abnormalities (e.g., congenital long QT syndrome; heart block); electrolyte disturbances (hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 100 mg daily. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised if methadone is prescribed in combination with ciprofloxacin.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66166/', '', 'Diamorphine, Acamprosate, Naltrexone, Disulfiram, Nalmefene, Codeine, Naltrexone, Oxymorphone, Tapentadol, Dezocine, Opium, More', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Tetracycline, Polymyxin B, Gentamicin, Chlorhexidine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66080, 'Ciprofloxacin', 'Methotrexate', 'Moderate', 'Coadministration with ciprofloxacin or other quinolones may increase the plasma concentrations of methotrexate. The exact mechanism of interaction is unknown but may involve competitive inhibition of the renal tubular secretion of methotrexate.', 'DDInter', 'Caution is advised if quinolones must be used with methotrexate. The authors of the case report recommend that ciprofloxacin and related antibiotics not be coadministered with high-dose methotrexate. During concomitant use, patients should be monitored for altered pharmacologic effects of both drugs and the dosage(s) adjusted accordingly if necessary.', 'Excretion', 'Caution is advised if quinolones must be used with methotrexate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66167/', '', 'Tinidazole, Cefixime, Secnidazole, Chlorhexidine, Rifamycin, Chlorhexidine, Rifamycin, Netilmicin, Trifluridine, Chlorhexidine, Povidone-iodine', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Anakinra, More', 1767369485);
