INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66088, 'Ciprofloxacin', 'Metoprolol', 'Minor', 'The coadministration of ciprofloxacin and metoprolol may result in elevated plasma concentrations of the latter. The mechanism by which ciprofloxacin may increase metoprolol plasma concentrations is unknown. Ciprofloxacin has not been shown to inhibit the CYP450 2D6 isoenzyme, which is responsible for the metabolism of metoprolol and several other beta-blockers. Since pharmacodynamic effects were not described in the study, the clinical significance of the interaction is also unclear. Until further data are available, it may be appropriate to monitor patients for potentially increased pharmacologic effects of metoprolol during concomitant therapy with ciprofloxacin. Patients should be advised to notify their physician if they experience possible symptoms of increased beta blockade such as fainting, swelling of the extremities, weight gain, or slow pulse.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66175/', '', 'Nebivolol, Esmolol, Nebivolol, Carteolol, Penbutolol, Nebivolol, Penbutolol', 'Tinidazole, Secnidazole, Cefixime, Amikacin, Levofloxacin, Polymyxin B, Enoxacin, Levofloxacin, Nalidixic acid, Lomefloxacin, Grepafloxacin, More', 1767369485);
