INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66579, 'Cisplatin', 'Paclitaxel (protein-bound)', 'Moderate', 'The risk of neurotoxicity including peripheral neuropathy may be increased during concomitant use of paclitaxel and cisplatin. These agents are individually neurotoxic and may have additive effects during coadministration. In a phase I trial using escalating doses of paclitaxel and cisplatin as sequential infusions, myelosuppression was more profound when paclitaxel was administered after cisplatin than when cisplatin was administered after paclitaxel.', 'DDInter', 'Paclitaxel should be given before cisplatin when used in combination. Peripheral blood cell counts and neurologic examination should be performed regularly, and patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms. Patients should also seek medical attention if they experience signs and symptoms of neuropathy such as visual disturbances and burning, tingling, pain, or numbness in the hands and feet. Consideration should be given to dosage reductions or immediate discontinuation of these medications in patients who develop severe myelosuppression or peripheral neuropathy.', 'Synergism', 'Paclitaxel should be given before cisplatin when used in combination.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66666/', '', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Enasidenib, Aflibercept, Anagrelide, Panobinostat, Venetoclax, More', '', 1767369485);
