INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66937, 'Citalopram', 'Methadone', 'Major', 'Citalopram can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking citalopram with certain other drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'The use of citalopram is not recommended in patients receiving other drugs that prolong the QT interval. Citalopram is also not recommended in patients with congenital long QT syndrome, bradycardia, hypokalemia, hypomagnesemia, recent acute myocardial infarction, or uncompensated heart failure.', 'Synergism', 'The use of citalopram is not recommended in patients receiving other drugs that prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67024/', '', 'Nalmefene', '', 1767369485);
