INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66945, 'Citalopram', 'Methylene blue', 'Major', 'Coadministration of methylene blue with serotonergic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Current research suggests that methylene blue has structural properties similar to monoamine oxidase inhibitors (MAOIs). As such, it may enhance serotonergic effects by inhibiting serotonin metabolism.', 'DDInter', 'Serotonergic agents should not be used in patients receiving methylene blue intravenously. Most serotonergic psychiatric drugs should be stopped 1 to 2 weeks (i.e., 4 to 5 half-lives) prior to treatment with methylene blue if possible, while others such as fluoxetine may require discontinuation up to 5 weeks in advance due to its prolonged half-life.', 'Synergism', 'Serotonergic agents should not be used in patients receiving methylene blue intravenously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67032/', '', 'Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Deferoxamine, Levoleucovorin, Arginine, Methionine, Deferiprone, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66946, 'Citalopram', 'Methysergide', 'Moderate', 'Serotonin antagonists such as cyproheptadine and methysergide may diminish the pharmacologic effects of selective serotonin reuptake inhibitors (SSRIs) and other highly serotonergic agents (e.g., 5-hydroxytryptophan; buspirone; mirtazapine; nefazodone; sibutramine; St. John''s wort; trazodone; SNRIs such as duloxetine, desvenlafaxine, milnacipran, and venlafaxine). This pharmacodynamic interaction is sometimes exploited in the use of cyproheptadine and methysergide for the treatment of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Theoretically, serotonergic agents may also antagonize the pharmacologic effects of cyproheptadine and methysergide, although this effect has not been reported.', 'DDInter', 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67033/', '', 'Phenelzine, Desipramine, Tranylcypromine, Trimipramine, Maprotiline, Isocarboxazid, Amitriptyline, Nortriptyline, Amoxapine, Clomipramine, Imipramine, More', 'Rimegepant, Eptinezumab, Ubrogepant, Galcanezumab, Atogepant, Erenumab, Fremanezumab', 1767369485);
