INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (67320, 'Triamterene', 'Potassium citrate', 'Major', 'The concomitant use of potassium-sparing diuretics and potassium preparations may result in hyperkalemia.', 'DDInter', 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended. Caution is advised if coadministration is required, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, and/or a risk for dehydration. Serum potassium and renal function should be checked regularly, and oral potassium supplementation should be avoided in patients with severe congestive heart failure who have serum potassium levels above 3.5 mEq/L. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67407/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (67321, 'Trifluoperazine', 'Potassium citrate', 'Major', 'Concomitant use of agents with anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, the class IA antiarrhythmic disopyramide) may potentiate the risk of upper gastrointestinal injury associated with oral solid formulations of potassium citrate. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby creating a high localized concentration of potassium ions in the region of a dissolving tablet or capsule and increasing the contact time with GI mucosa. Solid formulations of potassium chloride have been associated with upper GI bleeding and small bowel ulceration, stenosis, perforation, and obstruction.', 'DDInter', 'The use of oral solid formulations of potassium citrate is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects. A liquid formulation of potassium citrate should be considered. Patients prescribed a solid oral formulation should be advised to discontinue potassium therapy and contact their physician if they experience potential symptoms of upper GI injury such as severe vomiting, abdominal pain, distention, and gastrointestinal bleeding.', 'Synergism', 'The use of oral solid formulations of potassium citrate is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67408/', '', 'Quetiapine, Asenapine, Haloperidol, Pimavanserin, Ziprasidone, Lumateperone, Iloperidone, Risperidone, Amisulpride, Aripiprazole, Droperidol, More', 'Potassium bicarbonate, Potassium gluconate', 1767369485);
