INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (67514, 'Cladribine', 'Reserpine', 'Moderate', 'Oral bioavailability and systemic exposure of cladribine may be increased by potent inhibitors of breast cancer resistance protein (BCRP) while intracellular distribution and renal elimination of cladribine may be altered by potent inhibitors of equilibrative nucleoside transporter 1 (ENT1) and concentrative nucleoside transporter 3 (CNT3). The mechanism involves inhibition of ENT1, CNT3, and/or BCRP as cladribine is a substrate of each of these transporter systems.', 'DDInter', 'Coadministration of potent ENT1, CNT3, or BCRP transporter inhibitors should be avoided during the 4 to 5-day oral cladribine treatment cycles. If coadministration is required, consider concomitant alternatives that exhibit no or minimal inhibition of ENT1, CNT3, or BCRP. If this is not possible, dose reduction to the minimum mandatory dose, separation in the timing of administration, and careful patient monitoring is recommended.', 'Absorption, Distribution, Excretion', 'Coadministration of potent ENT1, CNT3, or BCRP transporter inhibitors should be avoided during the 4 to 5-day oral cladribine treatment cycles.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67601/', '', 'Prazosin, Rauwolfia serpentina root, Hydralazine, Clonidine, Guanethidine, Deserpidine, Guanfacine, Rauwolfia serpentina root, Methyldopa, Clonidine, Deserpidine', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 1767369485);
