INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (67791, 'Clarithromycin', 'Lansoprazole', 'Moderate', 'Coadministration with clarithromycin may increase the plasma concentrations of lansoprazole. The proposed mechanism is clarithromycin inhibition of intestinal (first-pass) and hepatic metabolism of lansoprazole via CYP450 3A4. Although lansoprazole is primarily metabolized by CYP450 2C19 in the liver, 3A4-mediated metabolism is the predominant pathway in individuals who are 2C19-deficient (approximately 3% to 5% of the Caucasian and 17% to 20% of the Asian population). Additionally, inhibition of P-glycoprotein intestinal efflux transporter by clarithromycin may also contribute to the interaction, resulting in increased bioavailability of lansoprazole.', 'DDInter', 'Although lansoprazole is generally well tolerated, caution may be advised during coadministration with clarithromycin, particularly if higher dosages of one or both drugs are used. Dosage adjustment may be necessary in patients who experience excessive adverse effects of lansoprazole.', 'Metabolism', 'Although lansoprazole is generally well tolerated, caution may be advised during coadministration with clarithromycin, particularly if higher dosages of one or both drugs are used.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67878/', '', 'Tinidazole, Bismuth subcitrate potassium, Vonoprazan, Clarithromycin', 'Tinidazole, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Esomeprazole, Vonoprazan, Levofloxacin, Lansoprazole, Lincomycin, Troleandomycin, Dirithromycin', 1767369485);
