INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (67895, 'Clarithromycin', 'Panobinostat', 'Major', 'Panobinostat can cause dose-dependent prolongation of the QT interval. Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of panobinostat with potent CYP450 3A4 inhibitors that can also prolong the QT interval including ceritinib, clarithromycin, ketoconazole, posaconazole, saquinavir, telithromycin, and voriconazole should generally be avoided. If coadministration with these agents is required, the manufacturer recommends reducing the dose of panobinostat to 10 mg for the treatment of multiple myeloma, given in combination with bortezomib and dexamethasone. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting panobinostat therapy and periodically during treatment as clinically indicated. In the premarketing trial, ECGs were performed at baseline and prior to initiation of each cycle for the first 8 cycles. Panobinostat should not be started if baseline QTc is greater than 450 msec. Likewise, treatment should be interrupted in patients who develop QTc prolongation of 480 msec or greater until recovery to less than or equal to Grade 1, then resumed at a reduced dose. In case of recurrence, therapy should be withheld until recovery to less than or equal to Grade 1, then resumed at a further reduced dose if necessary. Permanently discontinue panobinostat therapy if Grade 3 or 4 QTc prolongation does not resolve.', 'Metabolism, Synergism', 'Concomitant use of panobinostat with potent CYP450 3A4 inhibitors that can also prolong the QT interval including ceritinib, clarithromycin, ketoconazole, posaconazole, saquinavir, telithromycin, and voriconazole should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67982/', '', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Ranitidine, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Niraparib, Porfimer sodium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (67896, 'Clarithromycin', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/67983/', '', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Phenyl salicylate, Ammonium chloride, Dimethyl sulfoxide, More', 'Tinidazole, Dexlansoprazole, Rabeprazole, Metronidazole, Vonoprazan, Amoxicillin, Pantoprazole, Misoprostol, Esomeprazole, Ranitidine, Sucralfate, More', 1767369485);
