INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68004, 'Clarithromycin', 'Tacrine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68091/', '', 'Memantine, Ginkgo biloba, Aducanumab', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68005, 'Clarithromycin', 'Tacrolimus', 'Major', 'Tacrolimus can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as macrolide and ketolide antibiotics may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Coadministration with certain macrolide or ketolide antibiotics may significantly increase the oral bioavailability of tacrolimus. The proposed mechanism is inhibition of tacrolimus metabolism via intestinal CYP450 3A4. Inhibition of P-glycoprotein (P-gp) efflux transporter in gut wall may also contribute.', 'DDInter', 'Caution is advised when tacrolimus is used with macrolide or ketolide antibiotics that significantly inhibit CYP450 3A4 such as clarithromycin, erythromycin, josamycin, and telithromycin. A preemptive dosage reduction for tacrolimus may be appropriate in some cases. Frequent monitoring of tacrolimus whole blood levels should be performed during and after discontinuation of macrolide antibiotic therapy, and the tacrolimus dosage adjusted accordingly. In addition, patients should be closely monitored for development of serious adverse effects such as nephrotoxicity, lymphoma and other malignancies, infections, diabetes, neurotoxicity (tremor, paraesthesia, encephalopathy, delirium, coma), hyperkalemia, QT prolongation, myocardial hypertrophy, and hypertension. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism, Synergism', 'Caution is advised when tacrolimus is used with macrolide or ketolide antibiotics that significantly inhibit CYP450 3A4 such as clarithromycin, erythromycin, josamycin, and telithromycin.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68092/', '', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Caffeine, Abrocitinib, Dupilumab, Tralokinumab, More', 'Bismuth subcitrate potassium, Lincomycin, Dirithromycin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68006, 'Clarithromycin', 'Tadalafil', 'Moderate', 'Coadministration with drugs that are potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension. In addition, some authorities recommend avoiding concomitant use of tadalafil during and for 2 weeks after treatment with itraconazole in patients with pulmonary arterial hypertension. For erectile dysfunction, tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours when taken on an as needed basis in patients treated concomitantly with a potent CYP450 3A4 inhibitor. When taken on a daily basis, the dose should not exceed 2.5 mg per day. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Concomitant use of tadalafil with potent 3A4 inhibitors is not recommended in patients being treated for pulmonary arterial hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68093/', '', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Phenyl salicylate, Ammonium chloride, Dimethyl sulfoxide, Metyrosine', 'Tinidazole, Tetracycline, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Vonoprazan, Levofloxacin, Dirithromycin, More', 1767369485);
