INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68488, 'Erythromycin', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate. The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis. Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic. If concomitant use is required, additional bowel cleansing and colonoscopy may be required. The recommended dosage and duration of treatment with sodium picosulfate should not be exceeded. In addition, patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Others', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68575/', '', 'Vitamin A, Isotretinoin, Fluorometholone, Tretinoin, Azelaic acid, Adapalene, Ganciclovir, Acyclovir, Famciclovir, Trifluridine, Chlorhexidine, Povidone-iodine', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Plecanatide, Lubiprostone, Methylcellulose, Prucalopride, Linaclotide, Naloxone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68489, 'Escitalopram', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics. The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that can prolong the QT interval, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; mefloquine; halofantrine; lumefantrine), and some narcotic analgesics (methadone; propoxyphene).', 'DDInter', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval. Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities. Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process. Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68576/', '', 'St. John''s Wort, Oxitriptan, Viloxazine, Tryptophan, Esketamine', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Naldemedine, More', 1767369485);
