INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (69400, 'Clindamycin', 'Succinylcholine', 'Moderate', 'Lincosamides (e.g., clindamycin, lincomycin) have demonstrated neuromuscular blocking activity in vitro and may augment the pharmacologic effects of neuromuscular blocking agents. Clinically, the interaction has been reported in isolated cases involving atracurium, pancuronium, pipecuronium, succinylcholine, and tubocurarine.', 'DDInter', 'Patients receiving neuromuscular blockers in conjunction with lincosamide therapy should be monitored closely during and after anesthesia for development of respiratory depression, and life support should be readily available if needed. Anticholinesterases and calcium may not completely antagonize the neuromuscular blockade induced by lincosamides.', 'Synergism', 'Patients receiving neuromuscular blockers in conjunction with lincosamide therapy should be monitored closely during and after anesthesia for development of respiratory depression, and life support should be readily available if needed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/69487/', '', 'Botulinum toxin type A', 'Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, Tretinoin, Erythromycin, Dapsone, Azelaic acid, Adapalene, Telithromycin, Troleandomycin, More', 1767369485);
