INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (70173, 'Clofarabine', 'Vandetanib', 'Moderate', 'Coadministration with vandetanib may increase the plasma concentrations of drugs that are substrates of organic cation transporter 2 (OCT2). The mechanism is inhibition of OCT2-mediated renal tubular secretion by vandetanib.', 'DDInter', 'Caution is advised when vandetanib is used with drugs that are substrates of OCT2, particularly those with a narrow therapeutic range such as metformin. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vandetanib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised when vandetanib is used with drugs that are substrates of OCT2, particularly those with a narrow therapeutic range such as metformin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/70260/', '', 'Methotrexate, Fluorouracil, Pemetrexed, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, More', 'Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Avapritinib, Ibrutinib, Tivozanib, Zanubrutinib, Lorlatinib, Ripretinib, Alpelisib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (70174, 'Clofarabine', 'Varicella Zoster Vaccine (Recombinant)', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/70261/', '', '', '', 1767369485);
