INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71525, 'Anisindione', 'Clopidogrel', 'Major', 'Concomitant use of clopidogrel and oral anticoagulants such as warfarin may increase the risk of bleeding. Although often prescribed (with or without low-dose aspirin) in patients with atrial fibrillation following percutaneous coronary intervention (PCI) or revascularization surgery to prevent myocardial ischemic events, the safety and efficacy of these agents in combination have not been thoroughly evaluated.', 'DDInter', 'Caution and close monitoring of the INR and other bleeding parameters are recommended if clopidogrel is used in combination with an oral anticoagulant. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution and close monitoring of the INR and other bleeding parameters are recommended if clopidogrel is used in combination with an oral anticoagulant.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71612/', '', 'Warfarin, Cilostazol, Dicoumarol, Antithrombin III human, Acenocoumarol, Selexipag, Anistreplase', '', 1767369485);
