INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71545, 'Dexlansoprazole', 'Clopidogrel', 'Minor', 'Coadministration of clopidogrel with dexlansoprazole does not appear to significantly alter the systemic exposure to the active metabolite of clopidogrel or the drug''s effect on platelet inhibition. The bioactivation of clopidogrel is mediated in part by CYP450 2C19. Since dexlansoprazole is also a substrate of CYP450 2C19, competitive inhibition could theoretically occur, which may lead to reduced formation of the active metabolite of clopidogrel and reduced therapeutic efficacy. No dosage adjustment of clopidogrel is necessary when administered with an approved dosage of dexlansoprazole.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71632/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Tinidazole, Bismuth subcitrate potassium, Levofloxacin, Rifabutin', 1767369485);
