INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71557, 'Enzalutamide', 'Clopidogrel', 'Moderate', 'Coadministration with potent inhibitors of CYP450 2C8 may significantly increase the plasma concentrations of enzalutamide, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The use of enzalutamide in combination with potent CYP450 2C8 inhibitors such as gemfibrozil should generally be avoided. If concomitant use is required, a reduction of the enzalutamide dosage to 80 mg once daily is recommended. Following discontinuation of the potent CYP450 2C8 inhibitor, the enzalutamide dosage should be returned to that used prior to initiation of the inhibitor.', 'Metabolism', 'The use of enzalutamide in combination with potent CYP450 2C8 inhibitors such as gemfibrozil should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71644/', '', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 'Darolutamide, Fulvestrant, Toremifene, Abarelix, Relugolix, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71558, 'Epoprostenol', 'Clopidogrel', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71645/', '', 'Protein C, Acenocoumarol, Selexipag, Epoprostenol, Treprostinil', 'Vorapaxar, Antithrombin III human, Protein C, Clopidogrel', 1767369485);
