INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71607, 'Clopidogrel', 'Mifepristone', 'Major', 'Concomitant use of anticoagulants may exacerbate mifepristone-induced vaginal bleeding in women. Prolonged, potentially heavy bleeding lasting approximately 9 to 30 days typically follows mifepristone use in termination of pregnancy. Because it is a progesterone receptor antagonist, mifepristone can also promote unopposed endometrial proliferation that may result in endometrium thickening, cystic dilatation of endometrial glands, and vaginal bleeding when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', 'DDInter', 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone treatment should be administered cautiously in women who are receiving anticoagulant therapy. Patients who experience vaginal bleeding should be referred to a gynecologist for further evaluation.', 'Synergism', 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71694/', '', 'Alteplase, Dipyridamole, Abciximab, Tirofiban, Caplacizumab, Acetylsalicylic acid, Iloprost, Streptokinase, Eptifibatide, Antithrombin III human, Tenecteplase, More', 'Ospemifene, Ulipristal, Prasterone, Bazedoxifene', 1767369485);
