INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71875, 'Clorazepic acid', 'Olanzapine', 'Major', 'The safety and efficacy of intramuscular olanzapine administered in combination with benzodiazepines have not been established. CNS- and/or cardiorespiratory-depressant effects may be increased during concomitant use of olanzapine and benzodiazepines, especially in elderly or debilitated patients.', 'DDInter', 'Caution is necessary when olanzapine is used in combination with benzodiazepines. Ambulatory patients should be made aware of the possibility of additive CNS effects and counseled to avoid activities requiring mental alertness until they know how these agents affect them. They should also be advised to avoid rising abruptly from a sitting or recumbent position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting. Concomitant administration of IM olanzapine and parenteral benzodiazepine has not been studied and is therefore not recommended. Patients given this combination when necessary should be closely monitored for excessive sedation and cardiorespiratory depression.', 'Synergism', 'Caution is necessary when olanzapine is used in combination with benzodiazepines.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71962/', '', 'Amisulpride, Pimavanserin', '', 1767369485);
