INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72112, 'Droperidol', 'Clozapine', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins), drugs known to increase the QT interval (e.g., phenothiazines, tricyclic antidepressants, antiarrhythmic agents, etc.), certain other drugs (benzodiazepines, volatile anesthetics, intravenous opiates), or alcohol abuse may increase the risk of prolonged QT syndrome.', 'DDInter', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72199/', '', 'Droperidol', 'Clozapine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72113, 'Drospirenone', 'Clozapine', 'Moderate', 'Limited data suggest that coadministration with contraceptive hormones may increase the plasma concentrations of clozapine. The exact mechanism of interaction is unknown but may involve inhibition of clozapine metabolism via CYP450 1A2, 2C19 and/or 3A4.', 'DDInter', 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms. Pharmacologic response to clozapine should be monitored more closely whenever an oral contraceptive is added to or withdrawn from therapy in patients stabilized on their existing antipsychotic regimen, and the clozapine dosage adjusted as necessary.', 'Metabolism', 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72200/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Prochlorperazine, Promazine, Thioridazine, Lumateperone, Pimozide, More', 'Megestrol acetate, Estetrol, Ulipristal, Estradiol, Megestrol acetate, Progesterone, Estradiol', 1767369485);
