INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72115, 'Duloxetine', 'Clozapine', 'Moderate', 'Duloxetine is a moderate inhibitor of CYP450 2D6 and may increase the plasma concentrations of drugs that are substrates of the isoenzyme. Conversely, many CYP450 2D6 substrates can also be competitive or noncompetitive inhibitors of the isoenzyme and may increase the plasma concentrations of duloxetine, which is partially metabolized by CYP450 2D6.', 'DDInter', 'Caution is advised if duloxetine must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range such as tricyclic antidepressants, phenothiazines, beta blockers, and class IC antiarrhythmic agents (e.g., propafenone, flecainide). A lower initial dosage, as well as clinical and laboratory monitoring, may be appropriate for some drugs.', 'Metabolism', 'Caution is advised if duloxetine must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range such as tricyclic antidepressants, phenothiazines, beta blockers, and class IC antiarrhythmic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72202/', '', 'Amisulpride, Pimavanserin', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan', 1767369485);
