INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72137, 'Escitalopram', 'Clozapine', 'Major', 'Escitalopram can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if escitalopram is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. When escitalopram is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Caution is recommended if escitalopram is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72224/', '', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72138, 'Esketamine', 'Clozapine', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72225/', '', 'Amisulpride', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan, Isoflurane, Nitrous oxide, Desflurane, Enflurane, Etomidate', 1767369485);
