INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72396, 'Clozapine', 'Oxazepam', 'Major', 'Benzodiazepines and clozapine may have additive effects on respiratory and cardiovascular function. Ataxia, excessive salivation, hypotension, collapse, respiratory depression, respiratory arrest, loss of consciousness, cardiac arrest, and sudden death have been reported. The mechanism is unknown and causality has not been clearly determined.', 'DDInter', 'Caution is advised if concurrent therapy is necessary. Vital signs should be closely monitored.', 'Synergism', 'Caution is advised if concurrent therapy is necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72483/', '', 'Amisulpride, Pimavanserin, Methotrimeprazine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72397, 'Clozapine', 'Oxcarbazepine', 'Moderate', 'Coadministration with oxcarbazepine may increase the plasma concentrations of drugs that are substrates of the CYP450 2C19 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by oxcarbazepine and its active metabolite 10-monohydroxy derivative, or MHD. In vivo, the plasma levels of phenytoin, a CYP450 2C19 substrate, increased by up to 40% when oxcarbazepine was given at dosages above 1200 mg/day.', 'DDInter', 'Caution is advised if higher dosages of oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oxcarbazepine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if higher dosages of oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72484/', '', 'Amisulpride', 'Lacosamide', 1767369485);
