INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72464, 'Clozapine', 'Probucol', 'Major', 'Clozapine has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can cause QT prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clozapine treatment alone has been associated with ventricular arrhythmia, torsade de pointes, cardiac arrest, and sudden death.', 'DDInter', 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval. Serum electrolytes, including potassium, magnesium and calcium, should be measured at baseline and periodically during treatment, and any abnormalities corrected prior to initiating clozapine.', 'Synergism', 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72551/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Clofibrate, Cerivastatin, Lomitapide, Colestipol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72465, 'Clozapine', 'Procainamide', 'Major', 'The use of clozapine with other potentially myelotoxic agents including procainamide may increase the risk and/or severity of hematologic toxicity. Clozapine alone is associated with a significant risk of agranulocytosis, defined as an absolute neutrophil count (ANC) of less than 500/mm3. Like other Class IA antiarrhythmic agents, procainamide can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as clozapine may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated. The concurrent use of procainamide with other medications that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', 'Synergism', 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72552/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Moricizine, Lidocaine, Bretylium, Mexiletine', 1767369485);
