INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72600, 'Clozapine', 'Triclabendazole', 'Major', 'Clozapine has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can cause QT prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clozapine treatment alone has been associated with ventricular arrhythmia, torsade de pointes, cardiac arrest, and sudden death.', 'DDInter', 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval. Serum electrolytes, including potassium, magnesium and calcium, should be measured at baseline and periodically during treatment, and any abnormalities corrected prior to initiating clozapine.', 'Synergism', 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72687/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Praziquantel, Oxamniquine', 1767369485);
