INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72752, 'Cobicistat', 'Nevirapine', 'Moderate', 'Cobicistat may increase the plasma concentrations of antiretroviral agents. The plasma concentrations of cobicistat may also be increased or reduced in the presence of antiretroviral agents. The proposed mechanism is cobicistat inhibition of the CYP450 3A4 isoenzyme, of which antiretroviral agents may be substrates, and the inhibition or induction of CYP450 3A4 by concomitant antiretroviral medications.', 'DDInter', 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor. In addition, cobicistat should not be used concomitantly with ritonavir due to their similar effects on CYP450 3A4. According to some authorities, use of the antiretroviral combinations of atazanavir-cobicistat or darunavir-cobicistat concomitantly with the CYP450 3A4 inducers efavirenz, etravirine, or nevirapine is also not recommended.', 'Metabolism', 'Cobicistat is not intended for use with more than one antiretroviral medication that requires pharmacokinetic enhancement, such as two protease inhibitors or elvitegravir in combination with a protease inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72839/', '', 'Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, Remdesivir, Famciclovir, More', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Telbivudine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72753, 'Cobicistat', 'Nilotinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme. Because nilotinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 'Concomitant use of nilotinib with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of nilotinib during and for 2 weeks after treatment with itraconazole. Should treatment with a potent inhibitor be required, the manufacturer recommends that nilotinib therapy be withheld temporarily. If concomitant use is unavoidable, a reduction of the nilotinib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the nilotinib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Concomitant use of nilotinib with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72840/', '', 'Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (72754, 'Cobicistat', 'Nimodipine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and blood pressure lowering effect of nimodipine, which is primarily metabolized by CYP450 3A4 in the intestinal mucosa and liver.', 'DDInter', 'The use of nimodipine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated because of a risk of significant hypotension.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72841/', '', 'Clevidipine, Celecoxib', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 1767369485);
