INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73095, 'Cocaine (topical)', 'Levobupivacaine', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', 'DDInter', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73182/', '', 'Prilocaine, Meloxicam', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73096, 'Cocaine (topical)', 'Linezolid', 'Major', 'Cocaine exerts an indirect sympathomimetic effect and may precipitate hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs), as has been reported with other indirect- or mixed-acting sympathomimetic agents such as amphetamine and ephedrine. The mechanism involves a synergistic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and decreased reuptake of catecholamines (indirect sympathomimetic activity).', 'DDInter', 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73183/', '', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Vancomycin, Metronidazole, Telavancin, Methenamine, Spectinomycin, Fosfomycin, More', '', 1767369485);
