INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73513, 'Colesevelam', 'Venetoclax', 'Moderate', 'Coadministration of bile acid sequestrants may reduce the absorption of venetoclax, possibly reducing its pharmacologic effect. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces.', 'DDInter', 'If concomitant use is required, venetoclax should be administered at least 4 to 6 hours after the bile acid sequestrant. Close clinical and laboratory monitoring may be considered during coadministration, since adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', 'If concomitant use is required, venetoclax should be administered at least 4 to 6 hours after the bile acid sequestrant.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73600/', '', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Ezetimibe, Niacin, Atorvastatin, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73514, 'Colesevelam', 'Vitamin A', 'Moderate', 'INTERVAL: Bile acid sequestrants and the phosphate binder, sevelamer, can decrease the absorption of fat-soluble vitamins A, D, E, and K. In non-clinical safety studies, rats administered colesevelam at doses greater than 30-fold the projected human clinical dose developed hemorrhage in association with vitamin K deficiency. In a crossover study involving healthy subjects, coadministration of sevelamer with calcitriol resulted in a significant reduction in bioavailability for calcitriol (calcitriol with sevelamer vs calcitriol alone: AUC 137 pg*h/mL vs 318 pg*h/mL and Cmax 40.1 pg/mL vs 49.7 pg/mL, respectively).', 'DDInter', 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73601/', '', 'Alteplase, Ascorbic acid, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Sorbitol, Caffeine, Edrophonium, Indocyanine green, Inulin, More', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Fluvastatin, Gemfibrozil, More', 1767369485);
