INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73516, 'Colesevelam', 'Warfarin', 'Moderate', 'Decreases in International Normalized Ratio (INR) have been reported during postmarketing use of colesevelam in patients receiving warfarin. The mechanism of interaction is unknown. In an in vivo drug interaction study, colesevelam had no effect on warfarin bioavailability during coadministration. However, the study did not evaluate warfarin pharmacodynamics.Bile acid sequestrants can decrease the absorption of fat-soluble vitamins like vitamin K, which may theoretically interfere with the pharmacologic effects of warfarin and similar anticoagulants.', 'DDInter', 'Patients receiving warfarin and other oral anticoagulants should have INR monitored frequently during initiation of colesevelam, then periodically thereafter.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73603/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Mipomersen, Evinacumab, Evolocumab, Probucol, Bempedoic acid, Alirocumab', 1767369485);
