INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73520, 'Colestipol', 'Cerivastatin', 'Moderate', 'INTERVAL: When administered concomitantly, cholestyramine has been reported to decrease the rate and extent of fluvastatin bioavailability. Other bile acid sequestrants may also decrease the bioavailability of other HMG-CoA reductase inhibitors. The mechanism is most likely due to adsorption of the HMG-CoA reductase inhibitor to the bile acid sequestrant. Separating administration times has been shown to decrease the interaction.', 'DDInter', 'If therapy with both agents is necessary, the HMG-CoA reductase inhibitor should be administered at least 4 hours after the bile acid sequestrant.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73607/', '', 'Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cerivastatin, Colesevelam, Alirocumab', 'Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Lovastatin, Cholestyramine, Rosuvastatin, Clofibrate, Colestipol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73521, 'Deferasirox', 'Colestipol', 'Moderate', 'Coadministration with cholestyramine may decrease the bioavailability and efficacy of deferasirox. The mechanism involves binding of cholestyramine to deferasirox and its glucuronide metabolites, which are secreted into the bile. This interferes with the enterohepatic recycling of deferasirox by preventing intestinal deconjugation of the glucuronide metabolites and subsequent reabsorption. In healthy volunteers, administration of cholestyramine (12 grams twice daily) 4 and 10 hours after a single dose of deferasirox resulted in a 45% decrease in deferasirox systemic exposure (AUC). The interaction has not been studied with other bile acid sequestrants.', 'DDInter', 'The concomitant use of deferasirox with cholestyramine should generally be avoided. Otherwise, consideration should be given to increasing the initial dosage of deferasirox by 50% with subsequent adjustments made according to serum ferritin levels and clinical response. It is not known whether other bile acid sequestrants interact similarly with deferasirox.', 'Metabolism, Absorption', 'The concomitant use of deferasirox with cholestyramine should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73608/', '', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Ezetimibe, Rosuvastatin, Clofibrate, Niacin, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73522, 'Deflazacort', 'Colestipol', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', 'DDInter', 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73609/', '', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', 'Betamethasone', 1767369485);
