INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73560, 'Colestipol', 'Prednisolone', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', 'DDInter', 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73647/', '', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Alirocumab', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Betamethasone, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Indomethacin, More', 1767369485);
