INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73584, 'Entecavir', 'Colistimethate', 'Moderate', 'Because entecavir is primarily eliminated by renal excretion, coadministration with drugs that affect renal function may alter the plasma concentrations of entecavir and/or the coadministered drug. In patients with renal impairment, the apparent oral clearance of entecavir has been shown to decrease as the creatinine clearance decreases.', 'DDInter', 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during therapy with entecavir.', 'Excretion', 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73671/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', '', 1767369485);
