INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73821, 'Inotuzumab ozogamicin', 'Prochlorperazine', 'Moderate', 'Inotuzumab ozogamicin may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of inotuzumab ozogamicin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use cannot be avoided or if inotuzumab ozogamicin is used in patients with other risk factors for QT prolongation. The manufacturer suggests obtaining electrocardiograms and serum electrolytes at baseline and regularly during treatment.', 'Synergism', 'Coadministration of inotuzumab ozogamicin with other drugs that can prolong the QT interval should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73908/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
