INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73896, 'Prochlorperazine', 'Maprotiline', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73983/', '', 'Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73897, 'Prochlorperazine', 'Maraviroc', 'Moderate', 'Maraviroc may potentiate the hypotensive effect of some medications.', 'DDInter', 'Caution is advised when maraviroc is prescribed to patients on concomitant medications known to lower blood pressure. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised when maraviroc is prescribed to patients on concomitant medications known to lower blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73984/', '', 'Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Lumateperone, Pimozide, Cariprazine, Paliperidone, Lurasidone, Pimavanserin, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73898, 'Prochlorperazine', 'Mazindol', 'Moderate', 'Phenothiazines may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of phenothiazines. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73985/', '', 'Thiothixene, Risperidone, Amisulpride, Quetiapine, Promazine, Lumateperone, Pimozide, Cariprazine, Paliperidone, Lurasidone, Methotrimeprazine, More', 'Dexfenfluramine, Naltrexone', 1767369485);
