INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73959, 'Prochlorperazine', 'Olodaterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74046/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Mometasone, Mometasone furoate, Beclomethasone dipropionate, Cromoglicic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (73960, 'Prochlorperazine', 'Olopatadine (nasal)', 'Moderate', 'Concurrent use of olopatadine nasal spray with alcohol or other central nervous system (CNS) depressants may result in additive impairment of mental alertness and performance. Somnolence has been reported in some patients using olopatadine nasal spray in clinical trials.', 'DDInter', 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74047/', '', 'Amisulpride, Pimavanserin', '', 1767369485);
