INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74161, 'Conivaptan', 'Astemizole', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of astemizole and terfenadine. The mechanism is conivaptan inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these antihistamines. Although the interaction has not been specifically studied, high plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors such as macrolide antibiotics and azole antifungal agents) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with conivaptan should be avoided. Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with conivaptan.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, use of these agents in patients treated with conivaptan should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74248/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Promethazine, More', 'Tolvaptan', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74162, 'Conivaptan', 'Bedaquiline', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', 'DDInter', 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74249/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Rifampicin, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, More', '', 1767369485);
