INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74167, 'Conivaptan', 'Cisapride', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of cisapride. The mechanism is conivaptan inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of cisapride. Although the interaction has not been specifically studied, high plasma levels of cisapride (e.g., due to high doses or interaction with other potent 3A4 inhibitors such as macrolide antibiotics and azole antifungal agents) have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death. In pharmacokinetic studies with other drugs that are primarily metabolized by CYP450 3A4 such as midazolam, simvastatin, and amlodipine, conivaptan has increased systemic exposure (AUC) by 2- to 3-fold.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the drug should be avoided in patients treated with conivaptan. Patients currently receiving cisapride should consider an interruption during administration of conivaptan, and allow an appropriate amount of time following completion of conivaptan therapy before resuming the medication.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the drug should be avoided in patients treated with conivaptan.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74254/', '', 'Metoclopramide', 'Tolvaptan', 1767369485);
