INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74363, 'Conivaptan', 'Rivaroxaban', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter. The risk of bleeding associated with rivaroxaban may be increased.', 'DDInter', 'Concomitant use of rivaroxaban with dual P-gp and potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of rivaroxaban during and for 2 weeks after treatment with itraconazole.', 'Metabolism, Absorption', 'Concomitant use of rivaroxaban with dual P-gp and potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74450/', '', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', '', 1767369485);
