INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74366, 'Conivaptan', 'Rucaparib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and the risk of adverse effects of rucaparib, which has been shown in vitro to be a substrate of the isoenzyme. However, the effects on the pharmacokinetics of rucaparib have not been evaluated in vivo.', 'DDInter', 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy with rucaparib.', 'Metabolism', 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74453/', '', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Mitotane, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74367, 'Conivaptan', 'Ruxolitinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Indication specific dose modifications should be made when ruxolitinib is coadministered with strong CYP450 3A4 inhibitors. The recommended starting dose for patients with Polycythemia vera (PV) coadministered potent CYP450 3A4 inhibitors is ruxolitinib 5 mg twice a day. For patients with MF or PV who are stabilized on ruxolitinib 10 mg twice a day or greater and starting a potent CYP450 3A4 inhibitor, the ruxolitinib dose should be reduced by 50% (rounded up to the closest available tablet strength). For patients with MF or PV stabilized on a dose of 5 mg twice a day and starting fluconazole (at a dose of 200 mg per day or less), the ruxolitinib dose should be reduced to 5 mg once a day. For patients with MF or PV stabilized on ruxolitinib 5 mg once a day, concomitant use of strong CYP450 3A4 inhibitors should be avoided or ruxolitinib therapy interrupted for the duration of strong CYP450 3A4 inhibitor use. For patients with for acute graft versus host disease (GVHD) coadministered strong CYP450 3A4 inhibitors, the ruxolitinib dose should be reduced to 5 mg once a day with concomitant ketoconazole use; however, no dose adjustments are necessary with other potent CYP450 3A4 inhibitors.', 'Metabolism', 'Indication specific dose modifications should be made when ruxolitinib is coadministered with strong CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74454/', '', 'Tolvaptan', 'Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74368, 'Conivaptan', 'Salmeterol', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic levels and pharmacologic effects of salmeterol, which is primarily metabolized by the isoenzyme. Because salmeterol prolongs the QT interval in a dose-dependent manner, high systemic levels of salmeterol may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended. Some authorities recommend avoiding concomitant use of salmeterol during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74455/', '', 'Tolvaptan', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Olodaterol, Cromoglicic acid, Ipratropium, Salbutamol, Orciprenaline, More', 1767369485);
