INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74370, 'Conivaptan', 'Saxagliptin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly alter the plasma concentrations of saxagliptin and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', 'DDInter', 'Some authorities advise against using saxagliptin in combination with potent CYP450 3A4 inhibitors. Other authorities recommend that the dosage of saxagliptin should be limited to 2.5 mg once daily when used with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'Some authorities advise against using saxagliptin in combination with potent CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74457/', '', 'Tolvaptan', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 1767369485);
