INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (74374, 'Conivaptan', 'Sildenafil', 'Moderate', 'Coadministration with conivaptan may significantly increase the plasma concentrations of phosphodiesterase-5 (PDE5) inhibitors. The mechanism is conivaptan inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these agents. Although the interaction has not been specifically studied, high plasma levels of PDE5 inhibitors (e.g., due to high doses or interaction with other potent 3A4 inhibitors such as protease inhibitors) have been associated with increased PDE5 adverse effects such as headache, flushing, dyspepsia, rhinitis, visual disturbances, hypotension, and even death. In pharmacokinetic studies with other drugs that are primarily metabolized by CYP450 3A4 such as midazolam, simvastatin, and amlodipine, conivaptan has increased systemic exposure (AUC) by 2- to 3-fold.', 'DDInter', 'Use of PDE5 inhibitors should preferably be avoided in patients treated with conivaptan. Patients currently using PDE5 inhibitors should consider an interruption during administration of conivaptan, and allow an appropriate amount of time following completion of conivaptan therapy before resuming these medications.', 'Metabolism', 'Use of PDE5 inhibitors should preferably be avoided in patients treated with conivaptan.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74461/', '', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Trichlormethiazide, Ciclopirox, More', 'Tolvaptan', 1767369485);
