INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75239, 'Doxorubicin', 'Verapamil', 'Moderate', 'In the presence of verapamil, doxorubicin accumulates within cells, the intracellular distribution of doxorubicin shifts from cytoplasm to nucleus, and the formation of doxorubicin-related DNA strand breaks is increased. In the presence of verapamil, doxorubicin peak plasma levels, terminal half-life, and volume of distribution are significantly increased, whereas plasma clearance is significantly decreased. Although verapamil can be used to enhance the cytotoxicity of doxorubicin and has been used to overcome tumor cell resistance to doxorubicin, it can also increase the risk of side effects.', 'DDInter', 'Close monitoring for doxorubicin toxicity (cardiac, hematologic, and hepatic) is recommended. A reduction in doxorubicin dosage may be necessary in some cases.', 'Distribution', 'Close monitoring for doxorubicin toxicity (cardiac, hematologic, and hepatic) is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75326/', '', 'Trandolapril, Moexipril', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin, Epirubicin', 1767369485);
