INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (75248, 'Edoxaban', 'Verapamil', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', 'DDInter', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil. This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.', 'Absorption', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/75335/', '', 'Enalapril, Amlodipine, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, More', 'Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Streptokinase, Tinzaparin, Vorapaxar, More', 1767369485);
